/**
 * @title Proof of Existence
 * @dev Notarization with Blockchain
 */
contract ProofOfExistence {
  // @dev The contract state
  mapping (bytes32 => bool) private proofs;

  // @dev Private helper function to get a document's SHA256 hash
  // *pure function
  function calculateProof(string memory document) private pure returns (bytes32) {
    return sha256(abi.encode(document));
  }

  // @dev Calculate and store the proof for a document in the contract state
  // *transactional function*
  function notarize(string memory document) public returns (bytes32) {
    bytes32 hash = calculateProof(document);
    require(proofs[hash] != true, "Document already notarized.");
    proofs[hash] = true;
    return hash;
  }

  // @dev Check if a document has been notarized
  // *read-only function*
  function checkDocument(string memory document) public view returns (bool) {
    return proofs[calculateProof(document)];
  }
}
